import React, { useEffect, useState } from "react";
import axios from "axios";
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";

const FlatManagement = () => {
    const [flats, setFlats] = useState([]);
    const [showForm, setShowForm] = useState(false);
    const [flat, setFlat] = useState({
        flatid: null, blockno: "", flatno: "", ownerid: "", tenantid: "", status: "", bhk: "", sqft: ""
    });
    const [isEditing, setIsEditing] = useState(false);

    useEffect(() => {
        fetchFlats();
    }, []);

    const fetchFlats = async () => {
        try {
            const response = await axios.get("http://localhost:8082/getFlats");
            setFlats(response.data);
        } catch (error) {
            console.error("Error fetching flats:", error);
        }
    };

    const handleChange = (e) => {
        setFlat({ ...flat, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const url = isEditing ? `http://localhost:8082/updateFlat/${flat.flatid}` : "http://localhost:8082/addFlat";
        const method = isEditing ? "PUT" : "POST";

        const response = await axios({
            method: method,
            url: url,
            data: flat,
        });

        alert(response.data.message);
        setFlat({ flatid: null, blockno: "", flatno: "", ownerid: "", tenantid: "", status: "", bhk: "", sqft: "" });
        setIsEditing(false);
        setShowForm(false);
        fetchFlats();
    };

    const handleEdit = (flat) => {
        setFlat(flat);
        setIsEditing(true);
        setShowForm(true);
    };

    const handleDelete = async (flatid) => {
        if (window.confirm("Are you sure you want to delete this flat?")) {
            await axios.delete(`http://localhost:8082/deleteFlat/${flatid}`);
            fetchFlats();
        }
    };

    return (
        <div className="manage-flats-container">
            <h2>Flat Management</h2>
            <div className="flats-container">
                {flats.map((f) => (
                    <Card key={f.flatid} style={{ width: "18rem", margin: "20px", padding: "10px" }}>
                        <Card.Body>
                            <Card.Title>Flat No: {f.flatno}</Card.Title>
                            <Card.Text>Block: {f.blockno}</Card.Text>
                            <Card.Text>Owner ID: {f.ownerid}</Card.Text>
                            <Card.Text>Tenant ID: {f.tenantid}</Card.Text>
                            <Card.Text>Status: {f.status}</Card.Text>
                            <Card.Text>BHK: {f.bhk}</Card.Text>
                            <Card.Text>Sqft: {f.sqft}</Card.Text>
                            <Button variant="warning" onClick={() => handleEdit(f)}>Edit</Button>{" "}
                            <Button variant="danger" onClick={() => handleDelete(f.flatid)}>Delete</Button>
                        </Card.Body>
                    </Card>
                ))}
            </div>
            <Button variant="success" style={{ position: "fixed", bottom: "20px", right: "20px" }} onClick={() => setShowForm(true)}>Add Flat</Button>
        </div>
    );
};

export default FlatManagement;
