import React, { Component } from 'react';
import axios from 'axios';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import Header from './Header';

class Clubhouse extends Component {
  constructor(props) {
    super(props);
    this.state = {
      facilities: [],
    };
  }
  componentDidMount() {
    axios.get('http://localhost:8082/clubhouse')
      .then(res => {
        this.setState({ facilities: res.data });
      })
      .catch(err => {
        console.error('Error fetching clubhouse facilities', err);
      });
  }
  
  handleBooking = (facilityId) => {
    axios.post(`http://localhost:8082/clubhouse/book/${facilityId}`)
      .then(res => {
        alert('Booking confirmed!');
        this.fetchFacilities();
      })
      .catch(err => {
        console.error('Error booking facility', err);
        alert('Failed to book facility. Please try again.');
      });
  };
  
  handleCancel = (facilityId) => {
    axios.post(`http://localhost:8082/clubhouse/cancel/${facilityId}`)
      .then(res => {
        alert('Booking canceled!');
        this.fetchFacilities();
      })
      .catch(err => {
        console.error('Error canceling booking', err);
        alert('Failed to cancel booking.');
      });
  };
  

  fetchFacilities = () => {
    axios.get('http://localhost:8082/clubhouse')
      .then(res => {
        this.setState({ facilities: res.data });
      })
      .catch(err => {
        console.error('Error fetching clubhouse facilities', err);
      });
  };

  

  render() {
    return (
      <div>
        <Header />
        <div style={{ marginTop: '100px', textAlign: 'center' }}>
          <h1>Clubhouse Facilities</h1>
        </div>

        <div style={{ display: 'flex', flexWrap: 'wrap', justifyContent: 'center', padding: '20px' }}>
          {this.state.facilities.length === 0 ? (
            <p>No facilities available</p>
          ) : (
            this.state.facilities.map((facility) => (
              <Card key={facility.id} style={{ width: '22rem', margin: '10px', padding: '15px' }}>
                <Card.Title style={{ textAlign: 'center' }}>{facility.name}</Card.Title>
                <Card.Text>{facility.description}</Card.Text>
                <Card.Text><strong>Availability:</strong> {facility.available ? "Available" : "Booked"}</Card.Text>
                <Button 
                  variant="primary" 
                  onClick={() => this.handleBooking(facility.id)}
                  disabled={!facility.available}
                >
                  {facility.available ? "Book Now" : "Unavailable"}
                </Button>
              </Card>
            ))
          )}
        </div>
      </div>
    );
  }
}

export default Clubhouse;
