import React, { Component, useState } from 'react';
import { BrowserRouter as Router, Route } from 'react-router-dom';
import axios from 'axios';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import Form from 'react-bootstrap/Form';
import Header from './Header';

class Bills extends Component {
  constructor(props) {
    super(props);
    this.state = {
      bills: [],
      showModal: false,
      newBill: { flat_id: '', water: '', gas: '', electricity: '' }
    };
  }

  componentDidMount() {
    var data = { user: this.props.user.userid };
    axios.post('http://localhost:8082/bills', data)
      .then(res => this.setState({ bills: res.data }))
      .catch(() => console.log('Error from Bills'));
  }

  handleShow = () => this.setState({ showModal: true });
  handleClose = () => this.setState({ showModal: false });
  handleChange = (e) => {
    this.setState({ newBill: { ...this.state.newBill, [e.target.name]: e.target.value } });
  };

  handleSubmit = () => {
    axios.post('http://localhost:8082/addBill', this.state.newBill)
      .then(() => {
        this.setState({ showModal: false });
        this.componentDidMount();
      })
      .catch(() => console.log('Error adding bill'));
  };

  render() {
    const { bills, showModal, newBill } = this.state;
    const billList = bills.length ? bills.map((bill, k) => <BillCard bill={bill} key={k} />) : "No Bills to Display!";
    return (
      <Router>
        <Route exact path='/dashboard/bills'>
          <div>
            <Header />
            <div style={{ marginTop: '100px' }}>
              <div className="ShowEventList" style={{ margin: 'auto', width: 'fit-content' }}>
                <center><h1>Bill Management</h1></center>
                <div className="list">{billList}</div>
              </div>
            </div>
            {this.props.user.role === 'association' && (
              <Button style={styles.fab} onClick={this.handleShow}>+</Button>
            )}

            <Modal show={showModal} onHide={this.handleClose}>
              <Modal.Header closeButton>
                <Modal.Title>Add Bill Details</Modal.Title>
              </Modal.Header>
              <Modal.Body>
                <Form>
                  <Form.Group>
                    <Form.Label>Flat ID</Form.Label>
                    <Form.Control name="flat_id" value={newBill.flat_id} onChange={this.handleChange} />
                  </Form.Group>
                  <Form.Group>
                    <Form.Label>Water (kL)</Form.Label>
                    <Form.Control name="water" type="number" value={newBill.water} onChange={this.handleChange} />
                  </Form.Group>
                  <Form.Group>
                    <Form.Label>Gas (Cylinder)</Form.Label>
                    <Form.Control name="gas" type="number" value={newBill.gas} onChange={this.handleChange} />
                  </Form.Group>
                  <Form.Group>
                    <Form.Label>Electricity (Units)</Form.Label>
                    <Form.Control name="electricity" type="number" value={newBill.electricity} onChange={this.handleChange} />
                  </Form.Group>
                </Form>
              </Modal.Body>
              <Modal.Footer>
                <Button variant="secondary" onClick={this.handleClose}>Close</Button>
                <Button variant="primary" onClick={this.handleSubmit}>Save Bill</Button>
              </Modal.Footer>
            </Modal>
          </div>
        </Route>
      </Router>
    );
  }
}

const styles = {
  fab: {
    position: 'fixed',
    bottom: '20px',
    right: '20px',
    borderRadius: '50%',
    width: '60px',
    height: '60px',
    fontSize: '24px',
    textAlign: 'center',
    lineHeight: '60px',
    backgroundColor: '#6a1b9a',
    color: 'white',
    border: 'none',
    cursor: 'pointer',
  }
};

function BillCard({ bill }) {
  return (
    <Card style={{ width: '22rem', marginTop: '20px', fontSize: '130%', color: 'black', padding: '15px', backgroundColor: '#f7e6ff' }}>
      <Card.Title style={{ textAlign: 'center', fontSize: '120%', color: 'black' }}>Flat {bill.flat_id}</Card.Title>
      <hr style={{ height: '4px' }} /><br />
      <Card.Text>
        <table>
          <tr><th>Utility</th><th>Units</th><th>Cost</th></tr>
          <tr><td>Water (kL)</td><td>{bill.water}</td><td>{/* cost calculation */}</td></tr>
          <tr><td>Gas (Cylinder)</td><td>{bill.gas}</td><td>{/* cost calculation */}</td></tr>
          <tr><td>Electricity</td><td>{bill.electricity}</td><td>{/* cost calculation */}</td></tr>
          <tr><td><b>Total Bill</b></td><td></td><td>Rs. {/* total cost */}</td></tr>
        </table>
      </Card.Text>
    </Card>
  );
}

export default Bills;
