import React, { useState, useEffect } from "react";

const ManageFlats = () => {
    const [flats, setFlats] = useState([]);
    const [showForm, setShowForm] = useState(false);
    const [flat, setFlat] = useState({
        flatid: null,
        blockno: "",
        flatno: "",
        ownerid: "",
        tenantid: "",
        status: "vacant",
        bhk: "",
        sqft: ""
    });
    const [isEditing, setIsEditing] = useState(false);

    useEffect(() => {
        fetchFlats();
    }, []);

    const fetchFlats = async () => {
        try {
            const response = await fetch("http://localhost:8082/getFlats");
            const data = await response.json();
            setFlats(data);
        } catch (error) {
            console.error("Error fetching flats:", error);
        }
    };

    const handleChange = (e) => {
        setFlat({ ...flat, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const url = isEditing 
            ? `http://localhost:8082/updateFlat/${flat.flatid}` 
            : "http://localhost:8082/addFlat";
        const method = isEditing ? "PUT" : "POST";

        const response = await fetch(url, {
            method: method,
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(flat),
        });

        const result = await response.json();
        alert(result.message);

        setFlat({ flatid: null, blockno: "", flatno: "", ownerid: "", tenantid: "", status: "vacant", bhk: "", sqft: "" });
        setIsEditing(false);
        setShowForm(false);
        fetchFlats();
    };

    const handleEdit = (flat) => {
        setFlat(flat);
        setIsEditing(true);
        setShowForm(true);
    };

    const handleDelete = async (flatid) => {
        if (window.confirm("Are you sure you want to delete this flat?")) {
            await fetch(`http://localhost:8082/deleteFlat/${flatid}`, { method: "DELETE" });
            fetchFlats();
        }
    };

    return (
        <div className="manage-flats-container">
            <h2 className="manage-flats-title">Flat Management</h2>

            <div className="flats-container">
                {flats.map((f) => (
                    <div key={f.flatid} className="flat-card">
                        <p><strong>Flat ID:</strong> {f.flatid}</p>
                        <p><strong>Block No:</strong> {f.blockno}</p>
                        <p><strong>Flat No:</strong> {f.flatno}</p>
                        <p><strong>Owner ID:</strong> {f.ownerid}</p>
                        <p><strong>Tenant ID:</strong> {f.tenantid || "None"}</p>
                        <p><strong>Status:</strong> {f.status}</p>
                        <p><strong>BHK:</strong> {f.bhk}</p>
                        <p><strong>Square Feet:</strong> {f.sqft}</p>
                        <button className="edit-btn" onClick={() => handleEdit(f)}>Edit</button>
                        <button className="delete-btn" onClick={() => handleDelete(f.flatid)}>Delete</button>
                    </div>
                ))}
            </div>

            <button className="floating-add-btn" onClick={() => setShowForm(true)}>+</button>

            {showForm && (
                <div className="modal-overlay">
                    <div className="modal-content">
                        <h3>{isEditing ? "Edit Flat" : "Add Flat"}</h3>
                        <form onSubmit={handleSubmit} className="flat-form">
                            <input type="text" name="blockno" placeholder="Block No" value={flat.blockno} onChange={handleChange} required />
                            <input type="text" name="flatno" placeholder="Flat No" value={flat.flatno} onChange={handleChange} required />
                            <input type="text" name="ownerid" placeholder="Owner ID" value={flat.ownerid} onChange={handleChange} required />
                            <input type="text" name="tenantid" placeholder="Tenant ID (optional)" value={flat.tenantid} onChange={handleChange} />
                            <select name="status" value={flat.status} onChange={handleChange}>
                                <option value="vacant">Vacant</option>
                                <option value="occupied">Occupied</option>
                                <option value="sale">For Sale</option>
                                <option value="rent">For Rent</option>
                            </select>
                            <input type="text" name="bhk" placeholder="BHK" value={flat.bhk} onChange={handleChange} required />
                            <input type="text" name="sqft" placeholder="Square Feet" value={flat.sqft} onChange={handleChange} required />
                            <button type="submit">{isEditing ? "Update Flat" : "Add Flat"}</button>
                        </form>
                        <button className="close-btn" onClick={() => setShowForm(false)}>Close</button>
                    </div>
                </div>
            )}
        </div>
    );
};

export default ManageFlats;
