import React, { Component } from 'react';
import axios from 'axios';
import { ComplaintsCard } from './Cards.js';
import Header from './Header';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import { FaCommentDots } from 'react-icons/fa';

class Complaints extends Component {
  constructor(props) {
    super(props);
    this.state = {
      complaints: [],
      role: props.role,
      showModal: false,
      showActionModal: false,
      selectedComplaint: null,
      actionMessage: ""
    };
  }

  componentDidMount() {
    this.fetchComplaints();
  }

  fetchComplaints = () => {
    axios.post('http://localhost:8082/dashboard/complaint', {
      role: this.props.role,
      id: this.props.id
    })
    .then(res => {
      this.setState({ complaints: res.data });
    })
    .catch(err => {
      console.log('Error fetching complaints');
    });
  };

  toggleModal = () => {
    this.setState({ showModal: !this.state.showModal });
  };

  toggleActionModal = (complaint) => {
    this.setState({ 
      showActionModal: !this.state.showActionModal,
      selectedComplaint: complaint,
      actionMessage: complaint?.actionMessage || ""
    });
  };

  handleActionChange = (event) => {
    this.setState({ actionMessage: event.target.value });
  };

  submitActionUpdate = () => {
    const { selectedComplaint, actionMessage } = this.state;

    axios.post('http://localhost:8082/dashboard/updatecomplaint', {
      complaintid: selectedComplaint.complaintid,
      actionMessage: actionMessage,
      status: "Resolved"
    })
    .then(res => {
      alert("Complaint updated successfully!");
      this.setState({ showActionModal: false });
      this.fetchComplaints();  // Refresh the list
    })
    .catch(err => {
      console.log('Error updating complaint');
    });
  };

  render() {
    const { complaints, role, showModal, showActionModal, actionMessage, selectedComplaint } = this.state;

    return (
      <div className="ShowServiceList complaintLedger label label-danger" style={{ marginTop: '100px' }}>
        <div className="container">
          <h2 className="display-4 text-center">Complaints Ledger</h2>
          <div className="list">
            {complaints.length === 0 ? (
              <center>
                <img alt='No complaints' src="https://cdn.dribbble.com/users/45405/screenshots/2353058/gif-006-box.gif" height="500px" width="500px"/>
              </center>
            ) : (
              complaints.map((comp, k) => (
                <div key={k} className="complaint-card">
                  <ComplaintsCard complaints={comp} role={role} />
                  <p><strong>Status:</strong> {comp.status || "Pending"}</p>
                  <p><strong>Action Details:</strong> {comp.actionMessage || "No action taken yet"}</p>

                  {role === "association" && (
                    <Button variant="warning" onClick={() => this.toggleActionModal(comp)}>
                      Update Action
                    </Button>
                  )}
                </div>
              ))
            )}
          </div>
        </div>

        <FaCommentDots className="floating-btn" size={70} onClick={this.toggleModal} />
        <PostComp show={this.state.showModal} handleClose={this.toggleModal} userid={this.props.id} />

        {/* Action Update Modal */}
        <Modal show={showActionModal} onHide={() => this.toggleActionModal(null)} centered>
          <Modal.Header closeButton>
            <Modal.Title>Update Complaint Action</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <label>Action Details</label>
            <textarea rows="4" cols="30" value={actionMessage} onChange={this.handleActionChange} required />
            <br /><br />
            <Button variant="dark" onClick={this.submitActionUpdate} block>Update</Button>
          </Modal.Body>
        </Modal>
      </div>
    );
  }
}

const PostComp = ({ show, handleClose, userid }) => {
  const submitHandler = (event) => {
    event.preventDefault();

    let data = {
      complaint: document.getElementById('complaint').value,
      flatno: document.getElementById('flatno').value,
      userid: userid
    };

    axios.post('http://localhost:8082/dashboard/postcomplaint', data)
      .then(res => {
        if (res.data === "Success") {
          alert("Complaint posted successfully!");
          handleClose();
          window.location.reload();
        }
      });
  };

  return (
    <Modal show={show} onHide={handleClose} centered>
      <Modal.Header closeButton>
        <Modal.Title>Post Your Complaint</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <form onSubmit={submitHandler}>
          <label>Complaint Details</label>
          <textarea rows="4" cols="30" id="complaint" required /><br /><br />
          <label>Flat Number</label>
          <input type="text" id="flatno" required /><br /><br />
          <Button variant="dark" type="submit" block>Post</Button>
        </form>
      </Modal.Body>
    </Modal>
  );
};

export { Complaints, PostComp };
