import React, { Component } from 'react';
import axios from 'axios';
import { Button } from 'react-bootstrap';

class ResolveComp extends Component {
  constructor(props) {
    super(props);
    this.state = {
      complaintid: this.props.match.params.id,
      actionMessage: "",
      status: "In Progress"
    };
  }

  handleInputChange = (event) => {
    this.setState({ actionMessage: event.target.value });
  };

  submitHandler = (event) => {
    event.preventDefault();

    let data = {
      complaintid: this.state.complaintid,
      actionMessage: this.state.actionMessage,
      status: this.state.status
    };

    axios.post('http://localhost:8082/complaint/resolve/action', data)
      .then(res => {
        if (res.data === "Success") {
          alert("Complaint updated successfully!");
          window.location.replace('/dashboard/complaint');
        }
      });
  };

  markAsResolved = () => {
    this.setState({ status: "Resolved" }, this.submitHandler);
  };

  render() {
    return (
      <div>
        <Button style={{ float: 'left', fontSize: '18px', marginLeft: '10%' }} variant="warning" onClick={() => window.location.replace('/dashboard/complaint')}><b>&larr; Back</b></Button>
        <br /><br /><br />
        <center>
          <h4>Resolve the Complaint</h4><br />
          <form className="resolvecomplaint" onSubmit={this.submitHandler}>
            <label>Action Details</label><br />
            <textarea rows='5' id="actionMessage" value={this.state.actionMessage} onChange={this.handleInputChange} /><br /><br />
            <Button variant="dark" type="submit">Send Action Message</Button>
            <Button variant="success" onClick={this.markAsResolved} style={{ marginLeft: '10px' }}>Mark as Resolved</Button>
          </form>
        </center>
      </div>
    );
  }
}

export default ResolveComp;
