import React, { useState, useEffect } from "react";

const ManageUsers = () => {
    const [users, setUsers] = useState([]);
    const [user, setUser] = useState({
        first_name: "",
        last_name: "",
        username: "",
        password: "",
        mobile: "",
        role: "tenant"
    });
    const [showForm, setShowForm] = useState(false);

    useEffect(() => {
        fetchUsers();
    }, []);

    const fetchUsers = async () => {
        const response = await fetch("http://localhost:8082/getUsers");
        const data = await response.json();
        setUsers(data);
    };

    const handleChange = (e) => {
        setUser({ ...user, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        await fetch("http://localhost:8082/addUser", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(user),
        });
        setShowForm(false);
        fetchUsers(); // Refresh user list
    };

    const handleEdit = (id) => {
        alert(`Edit user with ID: ${id}`);
        // Implement edit functionality here
    };

    return (
        <div className="manage-users-container">
            {/* Back Button */}
            <button className="back-button" onClick={() => window.history.back()}>‚Üê Back</button>

            <h2 className="manage-users-title">Manage Users</h2>

            {/* User List */}
            <div className="user-list">
                {users.map((u) => (
                    <div className="user-card" key={u.id}>
                        <p><strong>{u.first_name} {u.last_name}</strong></p>
                        <p>Username: {u.username}</p>
                        <p>Role: {u.role}</p>
                        <button className="edit-button" onClick={() => handleEdit(u.id)}>Edit</button>
                    </div>
                ))}
            </div>

            {/* Floating Add User Button */}
            <button className="add-user-button" onClick={() => setShowForm(true)}>+</button>

            {/* User Form Modal */}
            {showForm && (
                <div className="user-form-modal">
                    <div className="user-form">
                        <h3>Add User</h3>
                        <form onSubmit={handleSubmit}>
                            <input className="manage-users-input" type="text" name="first_name" placeholder="First Name" onChange={handleChange} required />
                            <input className="manage-users-input" type="text" name="last_name" placeholder="Last Name" onChange={handleChange} required />
                            <input className="manage-users-input" type="text" name="username" placeholder="Username" onChange={handleChange} required />
                            <input className="manage-users-input" type="password" name="password" placeholder="Password" onChange={handleChange} required />
                            <input className="manage-users-input" type="text" name="mobile" placeholder="Mobile" onChange={handleChange} required />
                            <select className="manage-users-select" name="role" onChange={handleChange}>
                                <option value="tenant">Tenant</option>
                                <option value="owner">Owner</option>
                                <option value="association">Association</option>
                                <option value="security">Security</option>
                                <option value="housekeeper">Housekeeper</option>
                            </select>
                            <div className="form-buttons">
                                <button className="manage-users-button" type="submit">Add User</button>
                                <button className="cancel-button" onClick={() => setShowForm(false)}>Cancel</button>
                            </div>
                        </form>
                    </div>
                </div>
            )}
        </div>
    );
};

export default ManageUsers;
