import React, { useState, useEffect } from "react";

const ManageUsers = () => {
    const [users, setUsers] = useState([]);
    const [user, setUser] = useState({
        first_name: "",
        last_name: "",
        username: "",
        password: "",
        mobile: "",
        role: "tenant"
    });
    const [editingUser, setEditingUser] = useState(null);
    const [showForm, setShowForm] = useState(false);

    useEffect(() => {
        fetchUsers();
    }, []);

    const fetchUsers = async () => {
        const response = await fetch("http://localhost:8082/getUsers");
        const data = await response.json();
        setUsers(data);
    };

    const handleChange = (e) => {
        setUser({ ...user, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        const url = editingUser ? `http://localhost:8082/editUser/${editingUser.id}` : "http://localhost:8082/addUser";
        const method = editingUser ? "PUT" : "POST";

        const response = await fetch(url, {
            method,
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(user),
        });
        const result = await response.json();
        alert(result.message);
        setShowForm(false);
        setEditingUser(null);
        fetchUsers();
    };

    const handleEdit = (user) => {
        setUser(user);
        setEditingUser(user);
        setShowForm(true);
    };

    return (
        <div>
            <h2>Manage Users</h2>
            <table border="1">
                <thead>
                    <tr>
                        <th>First Name</th>
                        <th>Last Name</th>
                        <th>Username</th>
                        <th>Mobile</th>
                        <th>Role</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {users.map((user) => (
                        <tr key={user.id}>
                            <td>{user.first_name}</td>
                            <td>{user.last_name}</td>
                            <td>{user.username}</td>
                            <td>{user.mobile}</td>
                            <td>{user.role}</td>
                            <td>
                                <button onClick={() => handleEdit(user)}>Edit</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>

            {showForm && (
                <div>
                    <h2>{editingUser ? "Edit User" : "Add User"}</h2>
                    <form onSubmit={handleSubmit}>
                        <input type="text" name="first_name" value={user.first_name} placeholder="First Name" onChange={handleChange} required />
                        <input type="text" name="last_name" value={user.last_name} placeholder="Last Name" onChange={handleChange} required />
                        <input type="text" name="username" value={user.username} placeholder="Username" onChange={handleChange} required />
                        <input type="password" name="password" placeholder="Password" onChange={handleChange} required />
                        <input type="text" name="mobile" value={user.mobile} placeholder="Mobile" onChange={handleChange} required />
                        <select name="role" value={user.role} onChange={handleChange}>
                            <option value="tenant">Tenant</option>
                            <option value="owner">Owner</option>
                            <option value="association">Association</option>
                            <option value="security">Security</option>
                            <option value="housekeeper">Housekeeper</option>
                        </select>
                        <button type="submit">{editingUser ? "Update User" : "Add User"}</button>
                    </form>
                </div>
            )}

            <button onClick={() => setShowForm(true)} style={{ position: "fixed", bottom: 20, right: 20, padding: "10px 20px", borderRadius: "50%" }}>+</button>
        </div>
    );
};

export default ManageUsers;
