import React, { useEffect, useState } from "react";
import axios from "axios";
import Card from "react-bootstrap/Card";
import Button from "react-bootstrap/Button";

const FlatManagement = () => {
  const [flats, setFlats] = useState([]);

  useEffect(() => {
    fetchFlats();
  }, []);

  const fetchFlats = async () => {
    try {
      const response = await axios.get("http://localhost:8082/getFlats");
      setFlats(response.data);
    } catch (error) {
      console.error("Error fetching flats:", error);
    }
  };

  const deleteFlat = async (flatid) => {
    try {
      await axios.delete(`http://localhost:8082/deleteFlat/${flatid}`);
      fetchFlats();
    } catch (error) {
      console.error("Error deleting flat:", error);
    }
  };

  return (
    <div className="flat-management-container">
      <h2>Flat Management</h2>
      <div className="flat-cards">
        {flats.map((flat) => (
          <Card key={flat.flatid} style={{ width: "18rem", margin: "20px", padding: "10px" }}>
            <Card.Body>
              <Card.Title>Flat No: {flat.flatno}</Card.Title>
              <Card.Text>Block: {flat.blockno}</Card.Text>
              <Card.Text>Owner ID: {flat.ownerid}</Card.Text>
              <Card.Text>Tenant ID: {flat.tenantid}</Card.Text>
              <Card.Text>Status: {flat.status}</Card.Text>
              <Card.Text>BHK: {flat.bhk}</Card.Text>
              <Card.Text>Sqft: {flat.sqft}</Card.Text>
              <Button variant="warning" onClick={() => console.log("Edit flat", flat.flatid)}>Edit</Button>{" "}
              <Button variant="danger" onClick={() => deleteFlat(flat.flatid)}>Delete</Button>
            </Card.Body>
          </Card>
        ))}
      </div>
      <Button variant="success" style={{ position: "fixed", bottom: "20px", right: "20px" }}>Add Flat</Button>
    </div>
  );
};

export default FlatManagement;
