import React, { useState, useEffect } from "react";
import { FaEdit } from "react-icons/fa";


const ManageUsers = () => {
    const [users, setUsers] = useState([]);
    const [user, setUser] = useState({
        first_name: "",
        last_name: "",
        username: "",
        password: "",
        mobile: "",
        role: "tenant"
    });
    const [showForm, setShowForm] = useState(false);

    useEffect(() => {
        fetchUsers();
    }, []);

    const fetchUsers = async () => {
        const response = await fetch("http://localhost:8082/getUsers");
        const data = await response.json();
        setUsers(data);
    };

    const handleChange = (e) => {
        setUser({ ...user, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        await fetch("http://localhost:8082/addUser", {
            method: "POST",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(user),
        });
        setShowForm(false);
        fetchUsers();
    };

    return (
        <div className="manage-users-container">
            <h2 className="manage-users-title">Manage Users</h2>

            <div className="user-grid">
                {users.map((u) => (
                    <div className="user-card" key={u.id}>
                        <h3>{u.first_name} {u.last_name}</h3>
                        <p><strong>Username:</strong> {u.username}</p>
                        <p><strong>Mobile:</strong> {u.mobile}</p>
                        <p><strong>Role:</strong> {u.role}</p>
                        <FaEdit className="edit-icon" />
                    </div>
                ))}
            </div>

            <button className="add-user-button" onClick={() => setShowForm(true)}>+</button>

            {showForm && (
                <div className="user-form-modal">
                    <div className="user-form">
                        <h3>Add User</h3>
                        <form onSubmit={handleSubmit}>
                            <input type="text" name="first_name" placeholder="First Name" onChange={handleChange} required />
                            <input type="text" name="last_name" placeholder="Last Name" onChange={handleChange} required />
                            <input type="text" name="username" placeholder="Username" onChange={handleChange} required />
                            <input type="password" name="password" placeholder="Password" onChange={handleChange} required />
                            <input type="text" name="mobile" placeholder="Mobile" onChange={handleChange} required />
                            <select name="role" onChange={handleChange}>
                                <option value="tenant">Tenant</option>
                                <option value="owner">Owner</option>
                                <option value="association">Association</option>
                                <option value="security">Security</option>
                                <option value="housekeeper">Housekeeper</option>
                            </select>
                            <div className="form-buttons">
                                <button type="submit">Add User</button>
                                <button type="button" onClick={() => setShowForm(false)}>Cancel</button>
                            </div>
                        </form>
                    </div>
                </div>
            )}
        </div>
    );
};

export default ManageUsers;
