import React from "react";
import { motion } from "framer-motion";

function Body() {
  return (
    <div>
      <section className="py-0" id="home">
        <div
          className="bg-holder d-none d-md-block"
          style={{
            backgroundImage: "url(../images/illustrations/hero-section.png)",
            backgroundPosition: "right bottom",
            backgroundSize: "contain",
          }}
        ></div>

        <div
          className="bg-holder d-block d-md-none"
          style={{
            backgroundImage: "url(../images/illustrations/hero-bg.png)",
            backgroundPosition: "right top",
            backgroundSize: "contain",
          }}
        ></div>

        <div className="container text-center text-md-start">
          <div className="row align-items-center min-vh-md-75">
            <motion.div 
              initial={{ opacity: 0, y: 50 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8 }}
              className="col-md-7 col-lg-6 py-6"
            >
              <h1 className="mt-6 mb-sm-4 display-4 fw-bold text-primary">
                Modern Living, <br className="d-block d-lg-none d-xl-block" />
                Effortless Apartment Management System
              </h1>
              <p className="mb-4 fs-1 text-muted">
                A one-stop portal for managing your society
              </p>
              <motion.button 
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.9 }}
                className="btn btn-warning px-4 py-2 fw-bold rounded-pill"
              >
                Get Started
              </motion.button>
            </motion.div>
          </div>
        </div>
      </section>

      <div className="container">
        <div className="row justify-content-center">
          <div className="col-auto mb-5 mb-md-7">
            <h1 className="fw-bold text-warning text-center">
              Our <span className="text-dark">Services</span>
            </h1>
          </div>
        </div>
        <div className="row">
          {["Property Management", "Complaint Forum", "Bill Management", "Clubhouse Facilities", "Events", "Housekeeping", "Security Ledger"].map((service, index) => (
            <motion.div 
              key={index} 
              initial={{ opacity: 0, scale: 0.8 }}
              animate={{ opacity: 1, scale: 1 }}
              transition={{ duration: 0.5, delay: index * 0.2 }}
              className="col-sm-6 col-lg-3 mb-4 text-center"
            >
              <div className="px-3 py-4 shadow-lg rounded bg-white">
                <img className="img-fluid mb-4" src={`../images/gallery/${service.replace(/\s+/g, "_")}.png`} width="100" alt={service} />
                <h3 className="h5 mb-3 text-primary fw-bold">{service}</h3>
                <p className="lh-lg text-muted">Effortlessly manage {service.toLowerCase()}.</p>
              </div>
            </motion.div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Body;
