import React, { Component } from 'react';
import axios from 'axios';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/Button';
import { FaPlus } from 'react-icons/fa'; // Import Plus Icon

class Bills extends Component {
  constructor(props) {
    super(props);
    this.state = { bills: [] };
  }

  componentDidMount() {
    this.fetchBills();
  }

  fetchBills = () => {
    axios.post('http://localhost:8082/bills', { user: this.props.user.userid })
      .then(res => this.setState({ bills: res.data }))
      .catch(err => console.log('Error fetching bills', err));
  };

  handleAddBill = () => {
    // Navigate to add bill form (or show modal)
    alert("Redirect to Add Bill Form");
  };

  render() {
    const { bills } = this.state;

    return (
      <div style={{ textAlign: "center", marginTop: "50px", position: "relative" }}>
        {/* Back Button */}
        <Button
          variant="secondary"
          style={{ position: "absolute", top: "20px", left: "20px" }}
          onClick={() => window.history.back()}
        >
          ‚Üê Back
        </Button>

        <h1>Bill Management</h1>

        <div style={{ display: "flex", flexWrap: "wrap", justifyContent: "center", gap: "20px" }}>
          {bills.length === 0 ? "No Bills to Display!" : bills.map((bill, k) => <BillCard key={k} bill={bill} />)}
        </div>

        {/* Floating Add Button */}
        <Button
          variant="primary"
          style={{
            position: "fixed",
            bottom: "20px",
            right: "20px",
            borderRadius: "50%",
            width: "60px",
            height: "60px",
            fontSize: "24px",
            display: "flex",
            alignItems: "center",
            justifyContent: "center"
          }}
          onClick={this.handleAddBill}
        >
          <FaPlus />
        </Button>
      </div>
    );
  }
}

function BillCard({ bill }) {
  const wcost = bill.water * 4;
  const gcost = bill.gas * 825;
  const ecost = bill.electricity > 100 ? (bill.electricity - 100) * 1.5 : 0;
  const maint = bill.area * 3;
  const total = wcost + gcost + ecost + maint;

  return (
    <Card style={{ width: '22rem', padding: '15px', backgroundColor: '#f7e6ff' }}>
      <Card.Title style={{ textAlign: 'center' }}>Flat {bill.flat_no}</Card.Title>
      <hr />
      <Card.Text>
        <table style={{ width: "100%" }}>
          <tbody>
            <tr><th>Utility</th><th>Units</th><th>Cost</th></tr>
            <tr><td>Water (kL)</td><td>{bill.water}</td><td>{wcost}</td></tr>
            <tr><td>Gas (cylinder)</td><td>{bill.gas}</td><td>{gcost}</td></tr>
            <tr><td>Electricity</td><td>{bill.electricity}</td><td>{ecost}</td></tr>
            <tr><td>Maintenance</td><td>-</td><td>{maint}</td></tr>
            <tr><td><b>Total</b></td><td>-</td><td>Rs. {total}</td></tr>
          </tbody>
        </table>
      </Card.Text>
    </Card>
  );
}

export default Bills;
